	};

	render() {
		let todoDataComponents = this.state.todosData.map((task) => (
			<TodoItem
				key={task.id}
				tData={task}
				handleChange={this.handleChange}
			/>
		));

		return (
			<div className='main'>
				<div className='todo-list'>{todoDataComponents}</div>
			</div>
		);
	}
}

export default Main;
