import React, { Component } from "react";
import TodoItem from "./todoItem";
import todosData from "./todosData";

class Main extends Component {
	constructor() {
		super();

		this.state = {
			todosData,
		};
	}

	handleChange = (id) => {
		console.log("hello");
		// this.setState((prevState) => {
		// 	const updatedTodos = prevState.todosData.map((item) => {
		// 		if (item.id === id) {
		// 			item.completed = !item.completed;
		// 		}
		// 		return item;
		// 	});

		// 	return {
		// 		todosData: updatedTodos,
		// 	};
		// });
	};

	render() {
		let todoDataComponents = this.state.todosData.map((task) => (
			<TodoItem
				key={task.id}
				tData={task}
				handleChange={this.handleChange}
			/>
		));

		return (
			<div className='main'>
				<div className='todo-list'>{todoDataComponents}</div>
			</div>
		);
	}
}

export default Main;
