import React, { Component } from "react";

class Counter extends Component {
	state = {
		count: 0,
	};

	style = {
		display: "block",
	};

	render() {
		return (
			<React.Fragment>
				<span style={this.style}>{this.state.count}</span>
				<button>Increment</button>
			</React.Fragment>
		);
   }
   
   formatCount()
   {
      const { count } = this.state;
       return count 
   }
}

export default Counter;
